// Get the article text from first input item
let article = ($input.first().json.output || "").toLowerCase();

// Master prohibited list
const prohibitedWords = [
  "treats", "cures", "heals", "prevents", "vaccine",
  "prescription-strength", "antibiotic", "anti-inflammatory",
  "eczema", "acne", "hair loss",
  "approved", "certified", "guarantee", "proven", "recommended by",
  "tested", "validated", "buy with confidence", "unlike other brands",
  "best selling", "hot item", "best seller",
  "mildew", "antibacterial", "anti-fungal", "virus", "microbial",
  "pathogen", "resistant", "non-toxic", "toxic", "fungicide", "fungal",
  "alcohol-free", "contamination", "deterioration",
  "safe", "brightening", "whitening", "exfoliating", "inflammation",
  "harmless", "detox", "flawless", "renewing", "renewal",
  "dandruff", "relaxing"
];

// Count words
let wordCount = article.trim().split(/\s+/).length;

// Check for prohibited words
let found = prohibitedWords.filter(word => article.includes(word));

let result = {
  wordCount: wordCount,
  minWordCountRequired: 3000
};

if (found.length > 0) {
  result.status = "FAIL";
  result.message = "Prohibited terms found";
  result.words = found;
} else if (wordCount < 3000) {
  result.status = "FAIL";
  result.message = `Word count too low: ${wordCount} (minimum 3000 required)`;
} else {
  result.status = "PASS";
  result.message = "Content passes all checks";
}

// Return in n8n-friendly format
return [result];

// ✅ Get all incoming inputs
const inputData = $input.all();

// 1️⃣ Get article JSON directly from Code node output
const articleItem = inputData.find(i => i.json?.title || i.json?.sections);
const articleNode = articleItem ? articleItem.json : {};

// Extract fields safely
const title = articleNode.title || null;
const introduction = articleNode.introduction || null;
const sections = Array.isArray(articleNode.sections) ? articleNode.sections : [];
const conclusion = articleNode.conclusion || null;
const references = articleNode.references || null;

// Ensure FAQs is always an array
let faqs = [];
if (Array.isArray(articleNode.faqs)) {
  faqs = articleNode.faqs;
} else if (articleNode.faqs) {
  faqs = [articleNode.faqs];
}

if (sections.length === 0) {
  throw new Error("Sections not found or not an array.");
}

// 2️⃣ Collect images from Drive inputs
const allImages = inputData
  .filter(i => i.json?.base64) // only image inputs
  .map(i => ({
    base64: i.json.base64,
    mimeType: i.json.mimeType || null,
    fileName: i.json.fileName || null,
  }));

// 3️⃣ Merge sections with images by index
let imageIndex = 0;
const mergedSections = sections.map(section => {
  const imageData = allImages[imageIndex] || null;
  if (imageIndex < allImages.length) imageIndex++;

  return {
    heading: section.heading || null,
    content: section.content || null,
    image: imageData,
  };
});

// 4️⃣ Return structured JSON
return [
  {
    json: {
      title,
      introduction,
      sections: mergedSections,
      conclusion,
      references,
      faqs,
    },
  },
];

