// Input: items[0].json.output contains the full markdown content
let raw = $('AI Agent1').first().json.output || '';

// Split by lines
let lines = raw.split(/\r?\n/);

let title = null;
let introduction = null;
let conclusion = null;
let references = null;
let faqs = [];
let sections = [];

let currentSection = null;

// Parse line by line
lines.forEach(line => {
    line = line.trim();

    if (line.startsWith('# ')) {
        // Main Title
        if (!title) {
            title = line.replace(/^# /, '').trim();
        }
    } else if (line.startsWith('## ')) {
        // Save previous section
        if (currentSection) {
            const h = currentSection.heading.toLowerCase();
            if (h === 'introduction') introduction = currentSection;
            else if (h === 'conclusion') conclusion = currentSection;
            else if (h === 'references') references = currentSection;
            else if (h === 'faq' || h.includes('frequently asked')) faqs.push(currentSection);
            else sections.push(currentSection);
        }

        // Start new section
        currentSection = { 
            heading: line.replace(/^## /, '').trim(), 
            content: '' 
        };

    } else if (line.length > 0) {
        // Content inside section
        if (currentSection) {
            currentSection.content += (currentSection.content ? '\n' : '') + line;
        }
    }
});

// Push the last section
