// n8n Code node
// Input: AI Agent output in $('AI Agent1').first().json.output

const raw = $('AI Agent1').first().json.output || '';

if (!raw || typeof raw !== 'string' || raw.trim().length === 0) {
  throw new Error('No article text found in AI Agent output.');
}

// --- Helper: Split sections by H2 ---
function splitSectionsByH2(text) {
  const normalized = text.replace(/\r\n/g, '\n').trim();

  // Grab title if it starts with "# "
  let title = null;
  let workingText = normalized;

  const titleMatch = normalized.match(/^#\s+(.+)\n?/);
  if (titleMatch) {
    title = titleMatch[1].trim();
    workingText = normalized.slice(titleMatch[0].length).trim();
  }

  // Split on "## Heading"
  const parts = workingText.split(/\n(?=##\s+)/);
  const sections = [];
  for (let part of parts) {
    const lines = part.split('\n');
    if (lines[0].startsWith('## ')) {
      const heading = lines[0].replace(/^##\s+/, '').trim();
      const content = lines.slice(1).join('\n').trim();
      sections.push({ heading, content });
    }
  }

  return { title, sections };
}

// --- Parse all sections ---
const { title, sections: allSections = [] } = splitSectionsByH2(raw);

// --- Buckets ---
let introduction = null;
let conclusion = null;
let references = null;
let faqs = null;
let sections = [];

// --- Classify sections ---
for (const sec of allSections) {
  const h = sec.heading.toLowerCase();

  if (h === 'introduction') {
    introduction = sec;
  } else if (h === 'conclusion') {
    conclusion = sec;
  } else if (h === 'references') {
    references = sec;
  } else if (h === 'faq' || h.includes('frequently asked questions')) {
    faqs = sec;
  } else {
    sections.push(sec);
  }
}

// ✅ Guarantee sections is always an array
if (!Array.isArray(sections)) {
  sections = [];
}

// --- Return structured JSON ---
return [{
  json: {
    title: title || null,
    introduction: introduction || null,
    sections, // always an array
    conclusion: conclusion || null,
    faqs: faqs || null,
    references: references || null
  }
}];

// ✅ Get blog data from previous node
const blog = $input.first().json;

if (!blog.title || !blog.sections) {
  throw new Error("Blog data missing required fields: title or sections.");
}

// ✅ Convert JSON sections into HTML body
let bodyHtml = "";

// Introduction
if (blog.introduction) {
  bodyHtml += `<p>${blog.introduction.replace(/\n/g, "<br/>")}</p>`;
}

// Sections
for (const section of blog.sections) {
  if (section.heading) {
    bodyHtml += `<h2>${section.heading}</h2>`;
  }
  if (section.content) {
    bodyHtml += `<p>${section.content.replace(/\n/g, "<br/>")}</p>`;
  }
  if (section.image?.base64) {
    bodyHtml += `<img src="data:${section.image.mimeType};base64,${section.image.base64}" alt="${section.image.fileName || "skincare image"}"/>`;
  }
}

// Conclusion
if (blog.conclusion?.content) {
  bodyHtml += `<h2>${blog.conclusion.heading || "Conclusion"}</h2>`;
  bodyHtml += `<p>${blog.conclusion.content.replace(/\n/g, "<br/>")}</p>`;
}

// References
if (blog.references?.content) {
  bodyHtml += `<h3>${blog.references.heading || "References"}</h3>`;
  bodyHtml += `<p>${blog.references.content.replace(/\n/g, "<br/>")}</p>`;
}

// FAQs
if (Array.isArray(blog.faqs) && blog.faqs.length > 0) {
  bodyHtml += `<h3>Frequently Asked Questions</h3>`;
  for (const faq of blog.faqs) {
    bodyHtml += `<h4>${faq.heading || "Q&A"}</h4>`;
    bodyHtml += `<p>${faq.content.replace(/\n/g, "<br/>")}</p>`;
  }
}

// ✅ Final Shopify payload
return [
  {
    json: {
      article: {
        title: blog.title,
        body_html: bodyHtml
      }
    }
  }
];

